import request from "@/utils/request";

export function getweatherAPI() {
    let data = {
        lat: "42.3601째 N",
        lon: "71.0589째 W",
        appid: "6857be6453cfd80ea1b1f25ea85f5068",
    }
    return request({
        url: `https://api.openweathermap.org/data/2.5/weather`,
        method: 'get',
        params: data,
    })
}
