    async getTimeData() {
      try {
        const res = await getTimeAPI();
        const utcDate = new Date(res.data.utc_datetime);
        this.offset = parseInt(res.data.utc_offset); // 获取当前时区的偏移量（秒）
        const localTime = new Date(utcDate.getTime() + this.offset * 1000); // 调整为本地时间
        // 设置时间
        this.hours = localTime.getUTCHours(); // 使用 UTC 小时
        this.minutes = localTime.getUTCMinutes(); // 使用 UTC 分钟
        this.seconds = localTime.getUTCSeconds(); // 使用 UTC 秒
        console.log("TimeData", this.time);
      } catch (error) {
        console.error(error);
      }
    },
    startTimer() {
      this.timer = setInterval(() => {
        this.seconds += 1;
        if (this.seconds >= 60) {
          this.seconds = 0;
          this.minutes += 1;
        }
        if (this.minutes >= 60) {
          this.minutes = 0;
          this.hours += 1;
        }
      }, 1000);
    }
  },
  mounted() {
    this.getTimeData();
    this.startTimer();
  },
  beforeDestroy() {
    clearInterval(this.timer); // 组件销毁时清除定时器
  }
