import "core-js/modules/es.array.push.js";
export default {
  props: {
    imageSrc: String
  },
  data() {
    return {
      img: new Image(),
      selection: {
        x: 50,
        y: 50,
        width: 100,
        height: 100,
        children: []
      },
      isResizing: false,
      resizeCorner: null,
      controlSize: 8,
      ctx: null,
      startX: 0,
      startY: 0
    };
  },
  watch: {
    imageSrc(newSrc) {
      if (newSrc) {
        this.loadImage(newSrc);
      }
    }
  },
  methods: {
    loadImage(src) {
      this.img.onload = this.setCanvasAndSelection;
      this.img.src = src;
    },
    setCanvasAndSelection() {
      const canvas = this.$refs.canvas;
      canvas.width = this.img.width;
      canvas.height = this.img.height;

      // 初始化选区
      this.selection = {
        x: this.img.width * 0.85,
        y: this.img.height * 0.85,
        width: this.img.width * 0.9,
        height: this.img.height * 0.9,
        children: []
      };
      this.drawCanvas();
    },
    drawCanvas(w, y) {
      if (this.img) {
        this.ctx.drawImage(this.img, 0, 0);
      }

      // 绘制灰色遮罩层
      this.drawOverlay(this.ctx);

      // 绘制当前选区
      this.ctx.strokeStyle = "red";
      this.ctx.lineWidth = 2;
      this.ctx.strokeRect(this.startX, this.startY, w, y);

      // 绘制选区
      this.ctx.strokeStyle = "red";
      this.ctx.lineWidth = 2;
      this.ctx.strokeRect(this.selection.x, this.selection.y, this.selection.width, this.selection.height);
      // 绘制所有已选区
      if (this.selection.children.length > 0) {
        this.selection.children.forEach(area => {
          this.ctx.strokeRect(area.x, area.y, area.width, area.height);
        });
      }

      // 绘制控制点
      this.drawControlPoints(this.ctx);
    },
    // 绘制灰色遮罩层
    drawOverlay(ctx) {
      const {
        x,
        y,
        width,
        height
      } = this.selection;
      ctx.fillStyle = "rgba(0, 0, 0, 0.8)"; // 半透明的灰色
      ctx.fillRect(0, 0, ctx.canvas.width, y); // 选区上方
      ctx.fillRect(0, y + height, ctx.canvas.width, ctx.canvas.height - (y + height)); // 选区下方
      ctx.fillRect(0, y, x, height); // 选区左侧
      ctx.fillRect(x + width, y, ctx.canvas.width - (x + width), height); // 选区右侧
    },
    // 绘制控制点
    drawControlPoints(ctx) {
      const {
        x,
        y,
        width,
        height
      } = this.selection;
      const controlPoints = [{
        x: x,
        y: y
      }, {
        x: x + width,
        y: y
      }, {
        x: x,
        y: y + height
      }, {
        x: x + width,
        y: y + height
      }];
      ctx.fillStyle = "blue";
      controlPoints.forEach(point => {
        ctx.beginPath();
        ctx.arc(point.x, point.y, this.controlSize / 2, 0, 2 * Math.PI);
        ctx.fill();
      });
    },
    // 检查点击位置是否在遮罩层区域内
    isInsideOverlay(x, y) {
      const {
        x: selX,
        y: selY,
        width,
        height
      } = this.selection;
      // 检查点击是否在选区外的遮罩区域内
      return x < selX || x > selX + width || y < selY || y > selY + height;
    },
    onMouseDown(event) {
      const canvasRect = this.$refs.canvas.getBoundingClientRect();
      this.startX = event.clientX - canvasRect.left;
      this.startY = event.clientY - canvasRect.top;

      // 检查是否点击了控制点
      const clickedCorner = this.getClickedCorner(this.startX, this.startY);

      // 检查点击是否在遮罩层外
      if (clickedCorner !== 0 && clickedCorner !== 1 && clickedCorner !== 2 && clickedCorner !== 3) {
        if (this.isInsideOverlay(this.startX, this.startY)) {
          return; // 如果点击在遮罩层区域内，不允许拖动或改变选区
        }
      }
      if (clickedCorner !== null) {
        this.isResizing = true;
        this.resizeCorner = clickedCorner;
      }

      // 更新指针样式
      this.updateCursor(this.startX, this.startY);

      // console.log("clickedCorner", clickedCorner);
    },
    // 清空画布
    clearCanvas() {
      this.ctx.clearRect(0, 0, this.canvasWidth, this.canvasHeight);
    },
    // 实时更新鼠标指针样式
    updateCursor(x, y) {
      const clickedCorner = this.getClickedCorner(x, y);
      if (clickedCorner !== -1) {
        // 根据不同的控制点设置鼠标样式
        switch (clickedCorner) {
          case 0:
            this.$refs.canvas.style.cursor = "nw-resize";
            break;
          case 1:
            this.$refs.canvas.style.cursor = "ne-resize";
            break;
          case 2:
            this.$refs.canvas.style.cursor = "sw-resize";
            break;
          case 3:
            this.$refs.canvas.style.cursor = "se-resize";
            break;
        }
      } else {
        this.$refs.canvas.style.cursor = "default";
      }
    },
    onMouseMove(event) {
      // if (!this.isResizing) return;
      const canvasRect = this.$refs.canvas.getBoundingClientRect();
      const x = event.clientX - canvasRect.left;
      const y = event.clientY - canvasRect.top;

      // // 仅在选区区域内进行拖动或缩放
      // if (this.isInsideOverlay(x, y)) return;

      // 更新鼠标指针样式
      this.updateCursor(x, y);
      if (this.resizeCorner !== -1) {
        // 仅更新缩放
        this.resizeSelection(x, y);
        this.drawCanvas();
      } else {
        const width = x - this.startX;
        const height = y - this.startY;
        this.drawCanvas(width, height);
      }
    },
    onMouseUp(e) {
      if (this.resizeCorner !== -1) {
        this.isResizing = false;
        this.resizeCorner = null;
        // 鼠标释放后重置为默认指针样式
        this.$refs.canvas.style.cursor = "default";
      } else {
        const rect = this.$refs.canvas.getBoundingClientRect();
        const endX = e.clientX - rect.left;
        const endY = e.clientY - rect.top;
        this.isResizing = false;
        this.resizeCorner = null;
        // console.log(this.selection.children);

        this.selection.children.push({
          x: this.startX,
          y: this.startY,
          width: endX - this.startX,
          height: endY - this.startY
        });
      }
    },
    getClickedCorner(x, y) {
      const {
        x: selX,
        y: selY,
        width,
        height
      } = this.selection;
      const corners = [{
        x: selX,
        y: selY
      }, {
        x: selX + width,
        y: selY
      }, {
        x: selX,
        y: selY + height
      }, {
        x: selX + width,
        y: selY + height
      }];
      return corners.findIndex(corner => Math.abs(corner.x - x) < this.controlSize && Math.abs(corner.y - y) < this.controlSize);
    },
    resizeSelection(x, y) {
      const {
        x: selX,
        y: selY
      } = this.selection;
      switch (this.resizeCorner) {
        case 0:
          // 左上角
          this.selection.width += this.selection.x - x;
          this.selection.height += this.selection.y - y;
          this.selection.x = x;
          this.selection.y = y;
          break;
        case 1:
          // 右上角
          this.selection.width = x - selX;
          this.selection.height += selY - y;
          this.selection.y = y;
          break;
        case 2:
          // 左下角
          this.selection.width += selX - x;
          this.selection.height = y - selY;
          this.selection.x = x;
          break;
        case 3:
          // 右下角
          this.selection.width = x - selX;
          this.selection.height = y - selY;
          break;
      }

      // 限制最小尺寸
      this.selection.width = Math.max(20, this.selection.width);
      this.selection.height = Math.max(20, this.selection.height);
    },
    sendSelection() {
      console.log(this.selection);
    },
    undoSelection() {
      if (this.selection.children.length > 0) {
        this.selection.children = this.selection.children.slice(0, -1);
        this.drawCanvas(); // Redraw the canvas
        // console.log(this.selection);
      }
    },
    resetSelection() {
      if (this.selection.children.length > 0) {
        this.selection.children = [];
        this.drawCanvas(); // Redraw the canvas
        // console.log(this.selection);
      }
    }
  },
  mounted() {
    this.ctx = this.$refs.canvas.getContext("2d");
    if (this.imageSrc) {
      this.loadImage(this.imageSrc);
    }
  }
};
