import axios from "axios";
export default {
  data() {
    return {
      seconds: 0,
      minutes: 0,
      hours: 0,
      timer: null,
      offset: 0 // 与UTC的偏移时间（秒）
    };
  },
  computed: {
    formattedTime() {
      const paddedMinutes = String(this.minutes).padStart(2, "0");
      const paddedSeconds = String(this.seconds).padStart(2, "0");
      return `${this.hours}:${paddedMinutes}:${paddedSeconds}`;
    }
  },
  methods: {
    fetchTime() {
      axios.get("https://worldtimeapi.org/api/timezone/America/New_York").then(response => {
        const utcDate = new Date(response.data.utc_datetime);
        this.offset = parseInt(response.data.utc_offset); // 获取当前时区的偏移量（秒）
        const localTime = new Date(utcDate.getTime() + this.offset * 1000); // 调整为本地时间

        // 设置时间
        this.hours = localTime.getUTCHours(); // 使用 UTC 小时
        this.minutes = localTime.getUTCMinutes(); // 使用 UTC 分钟
        this.seconds = localTime.getUTCSeconds(); // 使用 UTC 秒
      }).catch(error => {
        console.error("Error fetching time:", error);
      });
    },
    startTimer() {
      this.timer = setInterval(() => {
        this.seconds += 1;
        if (this.seconds >= 60) {
          this.seconds = 0;
          this.minutes += 1;
        }
        if (this.minutes >= 60) {
          this.minutes = 0;
          this.hours += 1;
        }
      }, 1000);
    }
  },
  mounted() {
    this.fetchTime();
    this.startTimer();
  },
  beforeDestroy() {
    clearInterval(this.timer); // 组件销毁时清除定时器
  }
};
