export default {
  props: {
    imageSrc: String
  },
  data() {
    return {
      img: null,
      selection: {
        x: 50,
        y: 50,
        width: 100,
        height: 100,
        children: []
      },
      isResizing: false,
      resizeCorner: null,
      controlSize: 8
    };
  },
  watch: {
    imageSrc(newSrc) {
      if (newSrc) {
        this.loadImage(newSrc);
      }
    }
  },
  methods: {
    loadImage(src) {
      this.img = new Image();
      this.img.onload = this.setCanvasAndSelection;
      this.img.src = src;
    },
    setCanvasAndSelection() {
      const canvas = this.$refs.canvas;
      canvas.width = this.img.width;
      canvas.height = this.img.height;

      // 初始化选区
      this.selection = {
        x: this.img.width * 0.25,
        y: this.img.height * 0.25,
        width: this.img.width * 0.5,
        height: this.img.height * 0.5
      };
      this.drawCanvas();
    },
    drawCanvas() {
      const canvas = this.$refs.canvas;
      const ctx = canvas.getContext("2d");
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      if (this.img) {
        ctx.drawImage(this.img, 0, 0);
      }

      // 绘制灰色遮罩层
      this.drawOverlay(ctx);

      // 绘制选区
      ctx.strokeStyle = "red";
      ctx.lineWidth = 2;
      ctx.strokeRect(this.selection.x, this.selection.y, this.selection.width, this.selection.height);

      // 绘制控制点
      this.drawControlPoints(ctx);
    },
    // 绘制灰色遮罩层
    drawOverlay(ctx) {
      const {
        x,
        y,
        width,
        height
      } = this.selection;
      ctx.fillStyle = "rgba(0, 0, 0, 0.5)"; // 半透明的灰色
      ctx.fillRect(0, 0, ctx.canvas.width, y); // 选区上方
      ctx.fillRect(0, y + height, ctx.canvas.width, ctx.canvas.height - (y + height)); // 选区下方
      ctx.fillRect(0, y, x, height); // 选区左侧
      ctx.fillRect(x + width, y, ctx.canvas.width - (x + width), height); // 选区右侧
    },
    drawControlPoints(ctx) {
      const {
        x,
        y,
        width,
        height
      } = this.selection;
      const controlPoints = [{
        x: x,
        y: y
      }, {
        x: x + width,
        y: y
      }, {
        x: x,
        y: y + height
      }, {
        x: x + width,
        y: y + height
      }];
      ctx.fillStyle = "blue";
      controlPoints.forEach(point => {
        ctx.beginPath();
        ctx.arc(point.x, point.y, this.controlSize / 2, 0, 2 * Math.PI);
        ctx.fill();
      });
    },
    onMouseDown(event) {
      const canvasRect = this.$refs.canvas.getBoundingClientRect();
      const x = event.clientX - canvasRect.left;
      const y = event.clientY - canvasRect.top;
      this.isResizing = true;

      // 检查是否点击了控制点
      const clickedCorner = this.getClickedCorner(x, y);
      if (clickedCorner !== null) {
        this.resizeCorner = clickedCorner;
      }
    },
    onMouseMove(event) {
      if (!this.isResizing) return;
      const canvasRect = this.$refs.canvas.getBoundingClientRect();
      const x = event.clientX - canvasRect.left;
      const y = event.clientY - canvasRect.top;

      // 仅更新缩放
      this.resizeSelection(x, y);
      this.drawCanvas();
    },
    onMouseUp() {
      this.isResizing = false;
      this.resizeCorner = null;
    },
    getClickedCorner(x, y) {
      const {
        x: selX,
        y: selY,
        width,
        height
      } = this.selection;
      const corners = [{
        x: selX,
        y: selY
      }, {
        x: selX + width,
        y: selY
      }, {
        x: selX,
        y: selY + height
      }, {
        x: selX + width,
        y: selY + height
      }];
      return corners.findIndex(corner => Math.abs(corner.x - x) < this.controlSize && Math.abs(corner.y - y) < this.controlSize);
    },
    resizeSelection(x, y) {
      const {
        x: selX,
        y: selY
      } = this.selection;
      switch (this.resizeCorner) {
        case 0:
          // 左上角
          this.selection.width += this.selection.x - x;
          this.selection.height += this.selection.y - y;
          this.selection.x = x;
          this.selection.y = y;
          break;
        case 1:
          // 右上角
          this.selection.width = x - selX;
          this.selection.height += selY - y;
          this.selection.y = y;
          break;
        case 2:
          // 左下角
          this.selection.width += selX - x;
          this.selection.height = y - selY;
          this.selection.x = x;
          break;
        case 3:
          // 右下角
          this.selection.width = x - selX;
          this.selection.height = y - selY;
          break;
      }

      // 限制最小尺寸
      this.selection.width = Math.max(20, this.selection.width);
      this.selection.height = Math.max(20, this.selection.height);
    },
    sendSelection() {
      const selectionData = {
        x: this.selection.x,
        y: this.selection.y,
        width: this.selection.width,
        height: this.selection.height
      };
      this.$emit("sendSelection", selectionData);
    }
  },
  mounted() {
    if (this.imageSrc) {
      this.loadImage(this.imageSrc);
    }
  }
};
